const firstName = msg.payload.firstName;
const lastName = msg.payload.lastName;
const info = msg.payload.info;
const authSettings = global.get('authSettings');

// Password Regex pattern checker
const containsNchar = new RegExp('(?=.{'+authSettings.passReq.mustContainMinCharOf+',})');
const containsOneUppercase = new RegExp('(?=.*[A-Z])');
const containsOneLowercase = new RegExp('(?=.*[a-z])');
const containsOneNumber = new RegExp('(?=.*[0-9])');
const containsOneSpecialChar = new RegExp('(?=.*[@$!%*?&])');

var authUsersEmailsArray = [];


//creat an arry of allready used emails
//get all keys(usernames) from the authUsers object first
const authUsersKeys = Object.keys(authUsers);
authUsersKeys.forEach(function(eachUserName) {
    let eachEmail = authUsers[eachUserName].email;
    authUsersEmailsArray.push(eachEmail);
});


//check that userName is not allready taken by another user in authUsers
if (authUsers.hasOwnProperty(userName) ) {
    msg.error = true;
    msg.topic = 'Username already in use';
    msg.payload = 'Please Choose another Username';
    msg.userSubmition = userSubmition;
    return msg;
}
if (userPassword !== confirmPassword) {
    msg.error = true;
    msg.topic = 'Passwords do not match';
    msg.payload = 'Please enter matching Passwords';
    msg.userSubmition = userSubmition;
    return msg;
}
if (!containsNchar.test(userPassword)) {
    msg.error = true;
    msg.topic = 'Password too short';
    msg.payload = 'Must be longer than '+ authSettings.passReq.mustContainMinCharOf.toString() +' characters';
    return msg;
}
if (authSettings.passReq.mustContainMinOneUppercase) {
    if (!containsOneUppercase.test(userPassword)) {
        msg.error = true;
        msg.topic = 'Password lacks uppercase character';
        msg.payload = 'Must contain at least one upper uppercase character. Like ABCDEFG';
        return msg;
    }

}
if (authSettings.passReq.mustContainMinOneLowercase) {
    if (!containsOneLowercase.test(userPassword)) {
        msg.error = true;
        msg.topic = 'Password lacks lowercase character';
        msg.payload = 'Must contain at least one lowercase character. Like abcdefg';
        return msg;
    }
}
if (authSettings.passReq.mustContainMinOneNumber) {
    if (!containsOneNumber.test(userPassword)) {
        msg.error = true;
        msg.topic = 'Password lacks a number';
        msg.payload = 'Must contain at least one number. Like 1234567';
        return msg;
    }
}
if (authSettings.passReq.mustContainMinOneSpecialChar) {
    if (!containsOneSpecialChar.test(userPassword)) {
        msg.error = true;
        msg.topic = 'Password lacks a special character';
        msg.payload = 'Must contain at least one of these special characters. @$!%*?&';
        return msg;
    }
}
if (authUsersEmailsArray.includes(userEmail)) {
    msg.error = true;
    msg.topic = 'Email already in use';
    msg.payload = 'Please recover account';
    msg.userSubmition = userSubmition;
    return msg;
}
if (true) {
