        msg.error = true;
        msg.topic = 'Password lacks uppercase character';
        msg.payload = 'Must contain at least one upper uppercase character. Like ABCDEFG';
        return msg;
    }

}
if (authSettings.passReq.mustContainMinOneLowercase) {
    if (!containsOneLowercase.test(userPassword)) {
        msg.error = true;
        msg.topic = 'Password lacks lowercase character';
        msg.payload = 'Must contain at least one lowercase character. Like abcdefg';
        return msg;
    }
}
if (authSettings.passReq.mustContainMinOneNumber) {
    if (!containsOneNumber.test(userPassword)) {
        msg.error = true;
        msg.topic = 'Password lacks a number';
        msg.payload = 'Must contain at least one number. Like 1234567';
        return msg;
    }
}
if (authSettings.passReq.mustContainMinOneSpecialChar) {
    if (!containsOneSpecialChar.test(userPassword)) {
        msg.error = true;
        msg.topic = 'Password lacks a special character';
        msg.payload = 'Must contain at least one of these special characters. @$!%*?&';
        return msg;
    }
}
if (true) {

    msg.error = false;
    msg.userSubmition = userSubmition;
    return msg;
}

